```
/** Legacy pattern type for old API versions */
@pattern("^[a-z0-9]{3,18}$")
scalar ResourceNameLegacy extends string;
model MyResource {
@key("resourceName")
@typeChangedFrom(Versions.v2, ResourceNameLegacy)
@pattern("^[a-zA-Z][a-zA-Z0-9]*(-[a-zA-Z0-9]+)*$")
name: string;
}
