```typespec
@armResourceOperations
interface Offerings {
  // Old operation (no filter)
  @removed(Versions.v2025_11_01_preview)
  listBySubscription is ArmListBySubscription<Offering>;

  // New operation (with filter)
  @added(Versions.v2025_11_01_preview)
  listBySubscription is ArmListBySubscription<Offering, Parameters = OfferingListParameters>;
